  ret.push('  .reply(');
  ret.push(res.statusCode.toString());
  ret.push(', ');
  ret.push(JSON.stringify(responseBody));
  if (res.rawHeaders) {
    ret.push(', ');
    ret.push(inspect(res.rawHeaders));
